require_once($_config['admin_path'] . 'modules/shopping_cart/frontend/includes/fedex-common.php');

//The WSDL is not included with the sample code.
//Please include and reference in $path_to_wsdl variable.
$path_to_wsdl = $_config['admin_path'] . "modules/shopping_cart/frontend/includes/RateService_v14.wsdl";

ini_set("soap.wsdl_cache_enabled", "0");

switch($_config['environment']) {
	case "live":
		$location = "https://ws.fedex.com:443/web-services";
		break;
	case "dev":
		$location = "https://wsbeta.fedex.com:443/web-services/rate";
		break;
}

 
$client = new SoapClient($path_to_wsdl, array('location' => $location, 'trace' => 1)); // Refer to http://us3.php.net/manual/en/ref.soap.php for more information

$request['WebAuthenticationDetail'] = array(
	'UserCredential' => array(
		'Key' => getProperty('key'), 
		'Password' => getProperty('password')
	)
); 
$request['ClientDetail'] = array(
	'AccountNumber' => getProperty('shipaccount'), 
	'MeterNumber' => getProperty('meter')
);
$request['TransactionDetail'] = array('CustomerTransactionId' => ' *** Rate Available Services Request v14 using PHP ***');
$request['Version'] = array(
	'ServiceId' => 'crs', 
	'Major' => '14', 
	'Intermediate' => '0', 
	'Minor' => '0'
);
$request['ReturnTransitAndCommit'] = true;

$request['RequestedShipment']['DropoffType'] = 'REGULAR_PICKUP'; // valid values REGULAR_PICKUP, REQUEST_COURIER, ...
$request['RequestedShipment']['ShipTimestamp'] = date('c');
// Service Type and Packaging Type are not passed in the request
$request['RequestedShipment']['Shipper'] = array(
	'Address'=>getProperty('address1')
);
$request['RequestedShipment']['Recipient'] = array(
	'Address'=>getProperty('address2')
);
$request['RequestedShipment']['ShippingChargesPayment'] = array(
	'PaymentType' => 'SENDER',
   	'Payor' => array(
		'ResponsibleParty' => array(
			'AccountNumber' => getProperty('billaccount'),
			'Contact' => null,
			'Address' => array(
				'CountryCode' => getProperty('CountryCode')
			)
		)
	)
);																
$request['RequestedShipment']['RateRequestTypes'] = 'ACCOUNT'; 
$request['RequestedShipment']['RateRequestTypes'] = 'LIST'; 
$request['RequestedShipment']['PackageCount'] = '1';
$request['RequestedShipment']['RequestedPackageLineItems'] = array(
	'0' => array(
		'SequenceNumber' => 1,
		'GroupPackageCount' => 1,
		'Weight' => array(
			'Value' => $total_weight,
	    	'Units' => "{$ship_prefs['weight_type']}"
	    )/*,
	    'Dimensions' => array(
	    	'Length' => 10,
	       	'Width' => 10,
	       	'Height' => 3,
	       	'Units' => 'IN'
	  	)*/
	)/*,
	'1' => array(
		'SequenceNumber' => 2,
		'GroupPackageCount' => 1,
	    'Weight' => array(
	    	'Value' => 2.0,
	        'Units' => 'LB'
	   	),
	 	'Dimensions' => array(
	 		'Length' => 10,
	        'Width' => 20,
	        'Height' => 10,
	     	'Units' => 'IN'
	     )
	)*/
);

try {
	if(setEndpoint('changeEndpoint')){
		$newLocation = $client->__setLocation(setEndpoint('endpoint'));
	}
	$response = $client ->getRates($request);
        
    if ($response -> HighestSeverity != 'FAILURE' && $response -> HighestSeverity != 'ERROR'){
        echo '<h2>Shipping Method:</h2>';
		echo "<form method='POST' id='frmFedex' name='frmFedex' action='{$_SERVER['REQUEST_URI']}' enctype='application/x-www-form-urlencoded'><input type='hidden' name='province' value='{$_SESSION['province']}' />";
        echo '<table border="1" width="100%">';
        echo '<tr><th style="padding-left:5px;">Service</th><th>Amount</th><th>Delivery Estimate</th></tr>';
		
		//offer local/in-house delivery
		echo "<tr><td><input type='radio' class='ship_radio' name='ship_method' data-price='". number_format(ecom_functions::calculate_shipping($total_weight, $ship_prefs),2,".",",") ."' value='{$ship_prefs['inhousemethod']}' />{$ship_prefs['inhousemethod']}</td><td>".number_format(ecom_functions::calculate_shipping($total_weight, $ship_prefs),2,".",",")."</td><td>{$ship_prefs['inhousemethod']}</td></tr>";
		echo "<tr><th colspan=3>FedEx</th></tr>";
		
		if(is_array($response -> RateReplyDetails)){
			foreach ($response -> RateReplyDetails as $rateReply){
				printRateReplyDetails($rateReply);
			}
		}else{
			printRateReplyDetails($response -> RateReplyDetails);
		}
		
		// add Canada Post functionality
		if($ship_prefs['canpost_on']==1) {
			include($_config['admin_path'] . 'modules/shopping_cart/frontend/includes/cp_getrates.php');
		}
		
		echo "<tr><td colspan=3 style='text-align:right;background:#fff;'><input type='submit' class='cartbutton' value='Add Shipping' /></td></tr>";
		
        echo '</table></form>'. Newline;
    	//printSuccess($client, $response);
    } else {
        echo '<h2>Shipping Method:</h2>';
		echo "<form method='POST' id='frmFedex' name='frmFedex' action='{$_SERVER['REQUEST_URI']}' enctype='application/x-www-form-urlencoded'>";
        echo '<table border="1" width="100%">';
        echo '<tr><th style="padding-left:5px;">Service</th><th>Amount</th><th>Delivery Estimate</th></tr>';
		
		//offer local/in-house delivery
		echo "<tr><td><input type='radio' class='ship_radio' name='ship_method' data-price='". number_format(ecom_functions::calculate_shipping($total_weight, $ship_prefs),2,".",",") ."' value='{$ship_prefs['inhousemethod']}' />{$ship_prefs['inhousemethod']}</td><td>".number_format(ecom_functions::calculate_shipping($total_weight, $ship_prefs),2,".",",")."</td><td>{$ship_prefs['inhousemethod']}</td></tr>";
		
		// add Canada Post functionality
		if($ship_prefs['canpost_on']==1) {
			include($_config['admin_path'] . 'modules/shopping_cart/frontend/includes/cp_getrates.php');
		}
		
		echo "<tr><td colspan=3 style='text-align:right;background:#fff;'><input type='submit' class='cartbutton' value='Add Shipping' /></td></tr>";
		
        echo '</table></form>'. Newline;
		echo "Fedex cannot be contacted at this time, we're sorry for the inconvenience.";
        //printError($client, $response);
    }
    
    writeToLog($client);    // Write to log file
} catch (SoapFault $exception) {
   printFault($exception, $client);
}



function printRateReplyDetails($rateReply){
		echo '<tr>';
		$serviceType = "<td><input type='radio' class='ship_radio' name='ship_method' data-price='" . number_format($rateReply->RatedShipmentDetails[0]->ShipmentRateDetail->TotalNetCharge->Amount,2,".",",") ."' value='".$rateReply -> ServiceType."' />".$rateReply -> ServiceType . "</td>";
		$amount = '<td>$' . number_format($rateReply->RatedShipmentDetails[0]->ShipmentRateDetail->TotalNetCharge->Amount,2,".",",") . '</td>';
		if(array_key_exists('TransitTime' ,$rateReply)){
			$deliveryDate= '<td>' . $rateReply->TransitTime . '</td>';
		} elseif (array_key_exists('DeliveryTimestamp',$rateReply)){
			$deliveryDate= '<td>' . date('Y-m-d',strtotime($rateReply->DeliveryTimestamp)) . '</td>';
		} else {
			$deliveryDate= '<td>ETA Not Available</td>';
		}
		echo $serviceType . $amount. $deliveryDate;
		echo '</tr>';
}

